const touches = [...document.querySelectorAll('.bouton')];
const listeKeycode = touches.map(touche => touche.dataset.key);
const ecran = document.querySelector('.terminal-line');
const terminal = document.getElementById('terminal');
console.log(listeKeycode);

const price = document.getElementById('price').value;
const products = document.getElementById('products').value;

import { FirstPersonControls } from '../jsm/controls/FirstPersonControls.js';

document.addEventListener('keydown', (event) => {
  const valeur = event.keyCode.toString();
  action(valeur);
  console.log(valeur);
});

document.addEventListener('click', (e) => {
   const valeur = e.target.dataset.key;
   action(valeur);
   console.log(valeur);
});

ecran.textContent = "";

const action = (valeur) => {
  if (listeKeycode.includes(valeur)) {
      switch (valeur) {
         case '8':
            ecran.textContent = "";
            break;
         case '13':
            console.log(ecran.innerHTML.length);
            if(ecran.innerHTML.length === 4) {
               // Redirection vers la page de paiement
               window.location.href = '/paymentCart.php?price=' + price + '&products=' + products;
               break;
            }
            break;
         case '27':
            window.location.href = '/panier.php';
            break;
         default:
            const indexKeycode = listeKeycode.indexOf(valeur);
            const touche = touches[indexKeycode];
            ecran.textContent += touche.innerHTML;

      }
  }
}